import React from "react";
import { Redirect, RouteProps, Switch, Route } from "react-router-dom";
import { useSdk } from "../service";
import { pathLogin } from "./paths";

function ProtectedRoutes(
  routeProps: RouteProps,
  { children }: React.HTMLAttributes<HTMLOrSVGElement>,
): JSX.Element {
  const initialized = useSdk().initialized;

  return initialized ? (
    <Switch>{children}</Switch>
  ) : (
    <Redirect
      to={{
        pathname: pathLogin,
        state: { redirectPathname: routeProps.location.pathname, redirectState: routeProps.location.state },
      }}
    />
  );
}

export default ProtectedRoutes;
