import React from "react";
import { Redirect, RouteProps, Switch, Route, SwitchProps } from "react-router-dom";
import { useSdk } from "../service";
import { pathLogin } from "./paths";

function ProtectedSwitch({ children, location }: SwitchProps): JSX.Element {
  return initialized ? (
    <Switch location={location}>{children}</Switch>
  ) : (
    <Redirect
      to={{
        pathname: pathLogin,
        state: { redirectPathname: routeProps.location.pathname, redirectState: routeProps.location.state },
      }}
    />
  );
}

export default ProtectedSwitch;
