import { Button, Typography } from "antd";
import React from "react";
import { useHistory } from "react-router-dom";
import { History } from "history";
import Center from "../../../theme/layout/Center";
import Stack from "../../../theme/layout/Stack";
import { pathHome } from "../../paths";
import failIcon from "./assets/failIcon.svg";
import successIcon from "./assets/successIcon.svg";
import "./OperationResult.less";

const { Text } = Typography;

function getResultContent(success: boolean, history: History) {
  if (success) {
    return [successIcon, "SuccessText", "Home", goToHome];
  }

  return [failIcon, "FailText", "Retry", history.goBack];
}

interface OperationResultProps {
  readonly success: boolean;
  readonly message: string;
}

function OperationResult({ success, message }: OperationResultProps): JSX.Element {
  const history = useHistory();

  function goToHome() {
    history.push(pathHome);
  }

  const resultIcon = success ? successIcon : failIcon;
  const textClass = success ? "SuccessText" : "FailText";
  const buttonText = success ? "Home" : "Retry";
  const buttonAction = success ? goToHome : history.goBack;

  return (
    <Center tag="main" className="OperationResult">
      <Stack>
        <img src={resultIcon} alt="Result icon" />
        <Text className={textClass}>{message}</Text>
        <Button type="primary" onClick={() => buttonAction()}>
          {buttonText}
        </Button>
      </Stack>
    </Center>
  );
}

export default OperationResult;
