import { Button, Typography } from "antd";
import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import Center from "../../../theme/layout/Center";
import Stack from "../../../theme/layout/Stack";
import YourAccount from "../../components/YourAccount";
import { pathLuxury } from "../../paths";
import "./Home.less";
import { useAccount, useSdk, useError } from "../../../service";
import Loading from "../../components/Loading";
import { config } from "../../../config";
import { Contract } from "@cosmjs/cosmwasm";

const { Title, Text } = Typography;

function Home(): JSX.Element {
  const sdk = useSdk();
  const accountProvider = useAccount();
  const { setError } = useError();
  const [contracts, setContracts] = useState<readonly Contract[]>([]);

  useEffect(() => {
    sdk.init();
    accountProvider.refreshAccount();

    sdk
      .getClient()
      .getContracts(config.codeId)
      .then((contracts) => setContracts(contracts))
      .catch(setError);
  }, [sdk, accountProvider]);

  return sdk.loading ? (
    <Loading loadingText="Initializing app..." />
  ) : (
    <Center tag="main" className="Home">
      <Stack>
        <Title>Name Service</Title>
        {accountProvider.account && <Text>{`Your address: ${accountProvider.account.address}`}</Text>}
        <Stack tag="nav">
          {contracts.map(({ label, address }) => (
            <Link key={address} to={`/${label.toLowerCase()}/${address}`}>
              <Button type="primary">{label}</Button>
            </Link>
          ))}
          <Button disabled type="primary">
            Free
          </Button>
          <Link to={pathLuxury}>
            <Button type="primary">Luxury</Button>
          </Link>
          <Button disabled type="primary">
            Tokens
          </Button>
        </Stack>
        <YourAccount tag="footer" />
      </Stack>
    </Center>
  );
}

export default Home;
